  date,
  SUM(CASE WHEN parameters.name = 'meet:average_meeting_minutes' THEN (IFNULL(parameters.intValue,NULL)) ELSE 0 END) AS average_meeting_minutes,
  SUM(CASE WHEN parameters.name = 'meet:average_meeting_minutes_with_11_to_15_calls' THEN (IFNULL(parameters.intValue,NULL)) ELSE 0 END) AS average_meeting_minutes_with_11_to_15_calls,
  SUM(CASE WHEN parameters.name = 'meet:average_meeting_minutes_with_16_to_25_calls' THEN (IFNULL(parameters.intValue,NULL)) ELSE 0 END) AS average_meeting_minutes_with_16_to_25_calls,
