  SUM(CASE WHEN parameters.name = 'meet:num_calls_chromebase' THEN (IFNULL(parameters.intValue,NULL)) ELSE 0 END) AS num_calls_chromebase,
  SUM(CASE WHEN parameters.name = 'meet:num_calls_chromebox' THEN (IFNULL(parameters.intValue,NULL)) ELSE 0 END) AS num_calls_chromebox,
  SUM(CASE WHEN parameters.name = 'meet:num_calls_ios' THEN (IFNULL(parameters.intValue,NULL)) ELSE 0 END) AS num_calls_ios,
