  SUM(CASE WHEN parameters.name = 'meet:num_meetings_with_26_to_50_calls' THEN (IFNULL(parameters.intValue,NULL)) ELSE 0 END) AS num_meetings_with_26_to_50_calls,
  SUM(CASE WHEN parameters.name = 'meet:num_meetings_with_2_calls' THEN (IFNULL(parameters.intValue,NULL)) ELSE 0 END) AS num_meetings_with_2_calls,
