  SUM(CASE WHEN parameters.name = 'meet:total_call_minutes_jamboard' THEN (IFNULL(parameters.intValue,NULL)) ELSE 0 END) AS total_call_minutes_jamboard,
  SUM(CASE WHEN parameters.name = 'meet:total_call_minutes_unknown_client' THEN (IFNULL(parameters.intValue,NULL)) ELSE 0 END) AS total_call_minutes_unknown_client,
